INSERT INTO `gaz_biochemical_parameters` (`id`, `RiferimentoNormativo`, `Parte`, `ordine`, `Parametro`, `ValMinimoParametro`, `ValMassimoParametro`, `UnitaMisura`, `Note`, `Metodo`, `Settore`) VALUES
(1, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'A', 0, 'Enterococchi intestinali', NULL, '0', 'numero/100 ml', 'Per le acque confezionate in bottiglie o contenitori, l’unità di misura è «numero/250 ml»', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(2, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'A', 0, 'Escherichia coli (E. coli)', NULL, '0', 'numero/100 ml', 'Per le acque confezionate in bottiglie o contenitori, l’unità di misura è «numero/250 ml»', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(3, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Antimonio', NULL, '10', 'µg/l', '', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(4, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Arsenico', NULL, '10', 'µg/l', '', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(5, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Benzene', NULL, '1', 'µg/l', '', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(6, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Benzo(a)pirene', NULL, '0.01', 'µg/l', '', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(7, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Bisfenolo A', NULL, '2.5', 'µg/l', '', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(8, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Boro', NULL, '1.5', 'mg/l', 'Il valore di parametro di 2,4 mg/l si applica qualora l’acqua desalinizzata sia la principale fonte del sistema di fornitura in questione o in regioni in cui le condizioni geologiche potrebbero causare livelli elevati di boro nelle acque sotterranee.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(9, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Bromato', NULL, '10', 'µg/l', '', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(10, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Cadmio', NULL, '5', 'µg/l', '', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(11, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Clorato', NULL, '0.25', 'mg/l', 'Nei casi in cui il metodo di disinfezione usato non generi clorato, il valore di parametro di 0,25 mg/l deve essere soddisfatto al più tardi il 12 gennaio 2026. Nei casi in cui per la disinfezione si utilizza un metodo di disinfezione che genera clorato, in particolare diossido di cloro, si applica il valore di parametro di 0,70 mg/l. Ove possibile, i gestori idro-potabili si adoperano per applicare valori inferiori senza compromettere la disinfezione. Questo parametro è misurato esclusivamente se si utilizzano i metodi di disinfezione in questione.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(12, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Clorito', NULL, '0.25', 'mg/l', 'Nei casi in cui il metodo di disinfezione usato non generi clorito, il valore di parametro di 0,25 mg/l deve essere soddisfatto al più tardi il 12 gennaio 2026; fino al 11 gennaio 2026 il valore di parametro del clorito è pari a 0,7 mg/l. Nei casi in cui per la disinfezione si utilizza un metodo di disinfezione che genera clorito, in particolare diossido di cloro, si applica il valore di parametro di 0,70 mg/l. Ove possibile, i gestori idro-potabili si adoperano per applicare valori inferiori senza compromettere la disinfezione. Questo parametro è misurato esclusivamente se si utilizzano i metodi di disinfezione in', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(13, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Cromo', NULL, '25', 'µg/l', 'Il valore di parametro del cromo deve essere soddisfatto al più tardi il 12 gennaio 2026. Il valore di parametro del cromo fino al 11 gennaio 2026 è pari a 50 µg/l. Le regioni e province autonome, le Aziende sanitarie locali ed i gestori idro-potabili, ciascuno per quanto di competenza, devono provvedere affinché venga ridotta al massimo la concentrazione di cromo nelle acque destinate al consumo umano durante il suddetto periodo di transizione, per conformarsi al nuovo valore di parametro. Nell\'attuazione delle misure intese a garantire il raggiungimento del valore in questione deve darsi gradualmente priorità ai punti in cui la concentrazione di cromo nelle acque destinate al consumo umano è più elevata e l\'origine non è geogenica.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(14, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Rame', NULL, '2', 'mg/l', '', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(15, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Cianuro', NULL, '50', 'µg/l', 'Si considera il parametro cianuri totali.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(16, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, '1,2-dicloroetano', NULL, '3', 'µg/l', '', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(17, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Epicloridrina', NULL, '0.1', 'µg/l', 'Il valore di parametro di 0,10 µg/l si riferisce alla concentrazione monomerica residua nell’acqua calcolata a partire dal valore massimo della migrazione specifica desumibile dalle specifiche tecniche del corrispondente polimero a contatto con l’acqua, nei casi in cui detto polimero sia utilizzato nella filiera idro-potabile.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(18, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Fluoruro', NULL, '1.5', 'mg/l', '', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(19, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Acidi aloacetici (HAAs)', NULL, '60', 'µg/l', 'Questo parametro è misurato esclusivamente se per la disinfezione delle acque destinate al consumo umano si utilizzano metodi di disinfezione suscettibili di generare acidi aloacetici. Esso è la somma delle seguenti cinque sostanze rappresentative: acido monocloro-, dicloro-, e tricloro-acetico, acido mono- e dibromo-acetico.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(20, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Piombo', NULL, '5', 'µg/l', 'Il valore di parametro di 5,0 µg/l deve essere soddisfatto al più tardi entro il 12 gennaio 2036. Il valore di parametro per il piombo fino a tale data è 10 µg/l. Dopo tale data, il valore di parametro di 5,0 µg/l deve essere soddisfatto al punto di consegna.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(21, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Mercurio', NULL, '1', 'µg/l', '', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(22, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Microcistina-LR', NULL, '1', 'µg/l', 'È necessario che questo parametro sia misurato esclusivamente in caso di potenziali fioriture algali (crescita massiva di cellule cianobatteriche o potenziale formazione di efflorescenze) nell’acqua da destinare a consumo umano, secondo i criteri definiti nelle Linee guida per la gestione del rischio cianobatteri in acque destinate a consumo umano, Rapporti ISTISAN 11/35 Pt. 2 e s.m.i.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(23, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Nichel', NULL, '20', 'µg/l', '', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(24, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Nitrato', NULL, '50', 'mg/l', 'Deve essere soddisfatta la condizione: (Cnitrato/VPnitrato + Cnitrito/VPnitrito) ≤ 1 ove C e VP rappresentano, rispettivamente, la concentrazione in mg/l e il corrispondente valore di parametro per nitrato e nitrito.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(25, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Nitrito', NULL, '0.5', 'mg/l', 'Deve essere soddisfatta la condizione: (Cnitrato/VPnitrato + Cnitrito/VPnitrito) ≤ 1 ove C e VP rappresentano, rispettivamente, la concentrazione e il corrispondente valore di parametro per nitrato e nitrito. Per acque provenienti da impianti di trattamenti che possono generare lo ione nitrito – in particolare attraverso l’ossidazione dello ione ammonio, la riduzione dello ione nitrato o l’idrolisi delle cloroammine - si applica il valore di parametro di 0,10 mg/l al parametro nitrito.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(26, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Antiparassitari', NULL, '0.1', 'µg/l', 'Per «antiparassitari» s’intende: insetticidi organici erbicidi organici fungicidi organici nematocidi organici acaricidi organici alghicidi organici rodenticidi organici slimicidi organici prodotti connessi (tra l’altro regolatori della crescita) e relativi metaboliti ai sensi dell’articolo 3, punto 32, del regolamento (CE) n. 1107/2009 del Parlamento europeo e del Consiglio ritenuti pertinenti per le acque destinate al consumo umano. Un metabolita di antiparassitari è considerato pertinente per le acque destinate al consumo umano se esistono motivi per ritenere che possieda proprietà intrinseche, comparabili a quelle della sostanza madre, in termini di attività antiparassitaria bersaglio o che generi (esso stesso o i suoi prodotti di trasformazione) un rischio per la salute dei consumatori. Il valore di 0,10 µg/l si applica ad ogni singolo antiparassitario. Nel caso di aldrina, dieldrina, eptacloro ed eptacloro epossido, il valore di parametro è pari a 0,030 µg/l. Il monitoraggio è necessario solo per gli antiparassitari di cui è probabile la presenza in un determinato sistema di fornitura. Sulla base dei dati comunicati dagli Stati membri, la Commissione può istituire una banca dati di antiparassitari e dei relativi metaboliti tenendo conto della loro possibile presenza nelle acque destinate al consumo umano.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(27, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Antiparassitari -Totale', NULL, '0.5', 'µg/l', 'Per «antiparassitari — totale» si intende la somma di tutti i singoli antiparassitari sopra indicati, rilevati e quantificati nella procedura di monitoraggio.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(28, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'PFAS Totale', NULL, '0.5', 'µg/l', 'Per «PFAS — totale» si intende la totalità delle sostanze per- e polifluoroalchiliche. Tale valore di parametro si applica esclusivamente dopo l’elaborazione di orientamenti tecnici per il monitoraggio di tale parametro in conformità dell’articolo 12, comma 9. Le regioni e province autonome possono quindi decidere di utilizzare uno o entrambi i parametri «PFAS — totale» o «Somma di PFAS». L’Autorità sanitaria locale preposta al controllo della qualità delle acque destinate al consumo umano, sentita l’autorità sanitaria regionale e l’ISS, può adottare valori più restrittivi in specifiche circostanze territoriali, tenuto conto in particolare dell’esposizione pregressa alle sostanze per- e polifluoroalchiliche della popolazione interessata.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(29, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Somma di PFAS', NULL, '0.1', 'µg/l', 'Per «somma di PFAS» si intende la somma di tutte le sostanze per- e polifluoroalchiliche ritenute preoccupanti per quanto riguarda le acque destinate al consumo umano di cui all’allegato III, Parte B, punto 3. Si tratta di un sottoinsieme di sostanze «PFAS — totale» contenenti un Gruppo perfluoroalchilico con tre o più atomi di carbonio (vale a dire –CnF2n–, n ≥ 3) o un Gruppo perfluoroalchiletere con due o più atomi di carbonio (vale a dire –CnF2nOCmF2m–, n e m ≥ 1). L’Autorità sanitaria locale preposta al controllo della qualità delle acque destinate al consumo umano, sentita l’autorità sanitaria regionale e l’ISS può adottare valori più restrittivi in specifiche circostanze territoriali, tenuto conto in particolare dell’esposizione pregressa alle sostanze per- e polifluoroalchiliche della popolazione interessata.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(30, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Idrocarburi policiclici aromatici', NULL, '0.1', 'µg/l', 'Somma delle concentrazioni dei seguenti composti specifici: benzo(b) fluorantene, benzo(k)fluorantene, benzo(ghi)perilene e indeno(1,2,3- cd)pirene.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(31, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Selenio', NULL, '20', 'µg/l', 'Il valore di parametro di 30 µg/l si applica per le regioni e province autonome in cui le condizioni geologiche potrebbero comportare livelli elevati di selenio nelle acque sotterranee.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(32, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Tetracloroetilene e tricloroetilene', NULL, '10', 'µg/l', 'Somma delle concentrazioni di tali due parametri.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(33, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Trialometani -Totale', NULL, '30', 'µg/l', 'Ove possibile, i gestori idro-potabili si adoperano per applicare valori di parametro inferiori senza compromettere la disinfezione. Essa è la somma delle concentrazioni dei seguenti composti specifici: cloroformio, bromoformio, dibromoclorometano, e bromodiclorometano.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(34, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Uranio', NULL, '30', 'µg/l', '', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(35, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Vanadio', NULL, '140', 'µg/l', '', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(36, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'B', 0, 'Vinilcloruro', NULL, '0.5', 'µg/l', 'Il valore parametrico di 0,50 µg/l si riferisce alla concentrazione monomerica residua in acqua, calcolata a partire dal valore massimo della migrazione specifica desumibile dalle specifiche tecniche del corrispondente polimero a contatto con l’acqua, nei casi in cui detto polimero sia utilizzato nella filiera idro-potabile.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(37, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'C1', 0, 'Alluminio', NULL, '200', 'µg/l', '', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(38, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'C1', 0, 'Ammonio', NULL, '0.5', 'mg/l', '', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(39, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'C1', 0, 'Cloruro', NULL, '250', 'mg/l', 'L’acqua non deve essere corrosiva.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(40, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'C1', 0, 'Clostridium perfringens spore comprese', NULL, '0', 'Numero/100 ml', 'Questo parametro deve essere misurato se indicato come appropriato dalla valutazione del rischio.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(41, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'C1', 0, 'Colore', NULL, 'Accettabile per i consumatori e senza variazioni anomale', '', '', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(42, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'C1', 0, 'Conduttività', NULL, '2500', 'µS cm-1 a 20 °C', 'L’acqua non deve essere aggressiva.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(45, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'C1', 0, 'Concentrazione ioni idrogeno', '6.5', '9.5', 'Unità pH', 'L’acqua non deve essere aggressiva. Per le acque non frizzanti confezionate in bottiglie o contenitori il valore minimo può essere ridotto a 4,5 unità pH. Per le acque naturalmente ricche di anidride carbonica o arricchite artificialmente, il valore minimo può essere inferiore.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(46, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'C1', 0, 'Ferro', NULL, '200', 'µg/l', '', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(47, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'C1', 0, 'Manganese', NULL, '50', 'µg/l', '', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(48, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'C1', 0, 'Odore', NULL, 'Accettabile per i consumatori e senza variazioni anomale', '', '', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
