(50, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'C1', 0, 'Solfato', NULL, '250', 'mg/l', 'L’acqua non deve essere corrosiva.', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
(51, 'DECRETO LEGISLATIVO 23 febbraio 2023, n. 18.', 'C1', 0, 'Sodio', NULL, '200', 'mg/l', '', NULL, 'ACQUE DESTINATE AL CONSUMO UMANO'),
